const statuses = require('statuses');
const { middleware } = require('../../utils/wrappers');
/**
 * Provides the middleware to handle error responses for the app.
 */
class ErrorHandler {
  /**
   * Class constructor.
   * @param {Logger}           appLogger        To log the received errors.
   * @param {ResponsesBuilder} responsesBuilder To generate the JSON response.
   * @param {Boolean}          showErrors       If `false`, unknown errors will show a generic
   *                                            message instead of real message. And if `true`, it
   *                                            will not only show all kind of errors but it will
   *                                            also show the error stack.
   * @param {Class}            AppError         To validate if the received errors are known or not.
   */
  constructor(appLogger, responsesBuilder, showErrors, AppError) {
    /**
     * A local reference for the `appLogger` service.
     * @type {Logger}
     */
    this.appLogger = appLogger;
    /**
     * A local reference for the `responsesBuilder` service.
     * @type {ResponsesBuilder}
     */
    this.responsesBuilder = responsesBuilder;
    /**
     * Whether or not to show unknown errors real messages.
     * @type {Boolean}
     */
    this.showErrors = showErrors;
    /**
     * A local reference for the class the app uses to generate errors.
     * @type {Class}
     */
    this.AppError = AppError;
  }
  /**
   * Returns the Express middleware that shows the errors.
   * @return {ExpressMiddleware}
   */
  middleware() {
    return (err, req, res, next) => {
      // If the middleware received an error...
      if (err) {
        // Define the error response basic template.
        let data = {
          error: true,
          message: 'Oops! Something went wrong, please try again',
        };
        // Define the error response default status.
        let status = statuses['Internal Server Error'];
        // Validate if the error is known or not.
        const knownError = err instanceof this.AppError;
        // If the `showErrors` flag is enabled or the error is a known error...
        if (this.showErrors || knownError) {
          // ...set the error real message on the response.
          data.message = err.message;
          // If the error type is known...
          if (knownError) {
            // Try to get any extra information that should be included on the response.
            if (err.extras && err.extras.response) {
              data = Object.assign(data, err.extras.response);
            }
            // Try to obtain the response status from the error
            status = (err.extras && err.extras.status) || statuses['Bad Request'];
          }
          // If the `showErrors` flag is enabled...
          if (this.showErrors) {
            // Get the error stack and format it into an `Array`.
            const stack = err.stack.split('\n').map((line) => line.trim());
            //  Add the stack to the response.
            data.stack = stack;
            // Remove the first item of the stack, since it's the same as the message.
            stack.splice(0, 1);
            // Log the error.
            this.appLogger.error(`ERROR: ${err.message}`);
            this.appLogger.info(stack);
          }
        }
        // Send the response.
        this.responsesBuilder.json(res, data, status);
      } else {
        // ...otherwise, move to the next middleware.
        next();
      }
    };
  }
}
/**
 * This middleware generates responses for unhandled errors generated by the app.
 * @type {Middleware}
 */
const errorHandler = middleware((app) => {
  const debugging = app.get('appConfiguration').get('debug');
  const showErrors = debugging && debugging.showErrors;
  return new ErrorHandler(
    app.get('appLogger'),
    app.get('responsesBuilder'),
    showErrors,
    app.get('appError')
  )
  .middleware();
});

module.exports = {
  ErrorHandler,
  errorHandler,
};

const { middleware } = require('../../utils/wrappers');
/**
 * Force all the app traffice to be through HTTPS.
 */
class ForceHTTPS {
  /**
   * Class constructor.
   * @param {Array} [ignoredRoutes=[/^\/service\//]] A list of regular expressions to match routes
   *                                                 that should be ignored.
   */
  constructor(ignoredRoutes = [/^\/service\//]) {
    /**
     * A list of regular expressions to match routes that should be ignored.
     * @type {Array}
     */
    this.ignoredRoutes = ignoredRoutes;
  }
  /**
   * Returns the Express middleware that forces the redirection to HTTPS.
   * @return {ExpressMiddleware}
   */
  middleware() {
    return (req, res, next) => {
      if (
        !req.secure &&
        req.get('X-Forwarded-Proto') !== 'https' &&
        !this.ignoredRoutes.some((expression) => expression.test(req.originalUrl))
      ) {
        const host = req.get('Host');
        res.redirect(`https://${host}${req.url}`);
      } else {
        next();
      }
    };
  }
}
/**
 * Generates a middleware with an already defined list of ignored routes expressions.
 * @param {Array} ignoredRoutes A list of regular expressions to match routes that should be
 *                              ignored.
 * @return {Middleware}
 */
const forceHTTPSCustom = (ignoredRoutes) => middleware((app) => (
  app.get('appConfiguration').get('forceHTTPS') ?
    new ForceHTTPS(ignoredRoutes).middleware() :
    null
));
/**
 * A middleware to force HTTPS redirections to all the routes.
 * @type {Middleware}
 */
const forceHTTPS = forceHTTPSCustom();

module.exports = {
  ForceHTTPS,
  forceHTTPS,
  forceHTTPSCustom,
};

